Universe =
{
	{
		universeName = [[Introduction to Slipstream: The Price of Freedom]],
		universeText = [[The UNCG has been in control of most of humanity since Earth's Destruction in 2130. On the other side of the galaxy was the DSCG, a rival to the UNCG, and has been since the year 2145. The Two factions never had contact since. The Year is now 2300, and Emperor Alexander of the American Empire, has been elected chairman of the UNCG. His Plan is to take over the UNCG and erect the Empire of Humanity. Meanwhile, The DCSG, had found Ancient Tablets referring to massive kingdom made entirely of light. However, more secrets lie buried behind a region where the current UNCG resides. With the DOL and the UNCG Enemies, a war is imminent.]],
		universeToolTip = [[The Backstory]],
		universeImage = [[Data:UI\CreditsPics\intro.tga]],
	},
	{
		universeName = [[The United Nations Central Goverment]],
		universeText = [[The UNCG is the remnants of several countrys on earth which broke away from the United Nations. They formed a new entity, between them, which created an government spanning all of the Americas. Since then, it has grown corrupt over the years. The UNCG works by having smaller States, which can work both independent, or dependent of each other. They are controlled by a council made up of one house, and is controlled by a state every 4 years. Meaning each country has total control for four years. With Supreme domination over the region, the UNCG is considered a hyperpower.]],
		universeToolTip = [[The UNCG]],
		universeImage = [[Data:UI\CreditsPics\uncg.tga]],
	},
	{
		universeName = [[The Dominion of Light]],
		universeText = [[The Dominion of Light is a radical religious Faction that contains the remnants of The Russian Union, Greater China, and The New German Syndicate. It was formed after the DSCG, or Dominion of Socialistic Central Governments, fell to the radical religious cults. The DSCG was formed shortly after the UNCG, and was the second hyperpower on Earth at its peak. The DSCG has had troubled times, with a failing economy and famine on their planets, The DOL had no trouble seeking power in the former DSCG. ]],
		universeToolTip = [[The DOL]],
		universeImage = [[Data:UI\CreditsPics\dol.tga]],
	},
	{
		universeName = [[The Beginning of the End]],
		universeText = [[On October 28, 2300, The DOL attacked the UNCG shipping Carrier, after it returned a UNCG IFF code. The DOL quickly brought to bear two Battlecruisers and wiped the convoy out, but not before the carrier transmitted a distress call. The 22nd American Devision of the UNCG Guard was dispatched, and when they got to the place known as “The Bad Lands”, They found tons of wreckage, along with a DOL fleet. They Two immediately Began hacking away at each other. The Fight lasted 6 days and ended in UNCG victory. But later that week, the DOL sent fleet to the UNCG worlds, sparking a war that would last 400 years, with trillions of casualties.]],
		universeToolTip = [[The Start of the Freedom war]],
		universeImage = [[Data:UI\CreditsPics\badlands.tga]],
	},
	{
		universeName = [[Slipstreams and you - Part 1]],
		universeText = [[Slipstreams are corridors through higher-dimensional space. These dimensions are extremely small according to us, however, the upper realms are just as large as the universe in their geometry. Slipstreams work by having ships follow in the wake of small particles tunneling through these upper levels of space. Basically, its like surfing a wave. These particles are created by super-exciting a material named Valencium, named after the first American Empress.]],
		universeToolTip = [[How slipstreams work]],
		universeImage = [[Data:UI\CreditsPics\slipstream.tga]],
	},
	{
		universeName = [[Slipstreams and you - Part 2]],
		universeText = [[The material is made up of a variety of materials, and is usually found around stars with a very high level of UV and Gamma radiation. The wake of these particles are no bigger than the thickness of a sheet of paper, and act like a straw. As the particles move forward, the ship is drawn behind the particles in the wake, and ships cannot leave this wake unless power is cut to the drive. This is dangerous, and why slipgates are favored.]],
		universeToolTip = [[How slipstreams work]],
		universeImage = [[Data:UI\CreditsPics\slipstream.tga]],
	},
	{
		universeName = [[The Slipgate Network]],
		universeText = [[Slipgates are immense structures left behind by a civilization that inhabited the galaxy around 5 billion years ago. These gates are approximately 57.8 Kilometers in diameter, and are usually found in the middle of space. They utilize slipstreams in a way that humans have yet to comprehend. One can open a gate by inputing coordinates of another gate and enter the resulting slipstream. However, this is not simple, as one must manipulate the 3 rings to match the location according to the galactic plain. Humanity has only plotted 159 gates in the galaxy.]],
		universeToolTip = [[The Slipgates]],
		universeImage = [[Data:UI\CreditsPics\gate.tga]],
	},
	{
		universeName = [[The Hammer of Light]],
		universeText = [[The Hammer of Light is another ancient megalith. According to the DOL, the hammer will extinguish everything in the known universe and create a kingdom made entirely of light. However, the UNCG had found a shrine left by the slipgate builders, that said the hammer was a weapon that could destroy everything and recreate the universe. The location of the Hammer, is entirely unknown. However, the dol think that the hammer would lead them to salvation, but if they activate the hammer, they will destroy the universe! STOP THEM!]],
		universeToolTip = [[The Hammer of Light]],
		universeImage = [[Data:UI\CreditsPics\unk.tga]],
	},
}
